// This macro loops through all actors and items in the world and removes deprecated @dmgMod and @atkMod variables from damage and attack formulas.
///////////////////////////////////////////
// BACKUP YOUR WORLD BEFORE RUNNING THIS!!!
///////////////////////////////////////////

async function fixPower(power){
    await power.update({
            "system.attack.formula": power.system.attack.formula.replace(/\s*\+\s*@atkMod/i, ""),
            "system.hit.formula": power.system.hit.formula.replace(/\s*\+\s*@dmgMod/i, ""),
            "system.hit.critFormula": power.system.hit.critFormula.replace(/\s*\+\s*@dmgMod/i, "")
            });
    if (power.system.uses.per === ""){
        await power.update({
            "system.uses": {"value": "", "max": "", "per": null}
            }
        );
    }    
}

async function fixFormulas(){
    for (const item of game.items){
        if (item.type === "power"){
            await fixPower(item);
        }
    }

    for (const actor of game.actors){
        for (const item of actor.items){
            if (item.type === "power"){
                await fixPower(item);
            }
        }
    }
}

new Dialog({
  title: "Update v0.4 attack and damage formulas",
  content: "BACKUP YOUR WORLD BEFORE RUNNING THIS!",
  buttons: {
    yes: {
      label: "Run",
      callback: fixFormulas
    },
    close: {
      label: "Cancel"
    },
  },
  default: "close",
  close: () => {}
}).render(true);
